    if (isset($this->request->post['config_ajaxadvancedsearch_manufacturer_search'])) {
        $data['config_ajaxadvancedsearch_manufacturer_search'] = $this->request->post['config_ajaxadvancedsearch_manufacturer_search'];
    } else {
